    while (1) {
        QByteArray http = QHttpGet::getContentOfURL("http://money.finance.sina.com.cn/quotes_service/api/openapi.php/HK_MoneyFlow.getLatestMoneyFlowApp?type=north&flag=app");
        if(http.size() > 0)
        {
            //获取数组部分的数据
            QJsonDocument doc = QJsonDocument::fromJson(http);
            if(doc.isObject())
            {
                QJsonObject total_obj = doc.object();
                if(total_obj.contains("result"))
                {
                    QJsonValue result = total_obj.value("result");
                    if(result.isObject())
                    {
                        QJsonValue  data = result.toObject().value("data");
                        if(data.isArray())
                        {
                            //开始正式解析
                            QList<NorthBoundData> list;
                            int max = 5;
                            int min = 0;
                            QJsonArray array = data.toArray();
                            for(int i=0; i<array.size(); i++)
                            {
                                NorthBoundData north;
                                QJsonObject obj = array.at(i).toObject();
                                north.sh_flow = obj.value("sh_flow_money").toString().toDouble();
                                north.sz_flow = obj.value("sz_flow_money").toString().toDouble();
                                north.total_flow = north.sh_flow + north.sz_flow;

                                QTime time = QTime::fromString(obj.value("create_time").toString(), "hmm");
                                QDate date = QDate::fromString(obj.value("create_date").toString(), "yyyyMMdd");
                                north.time.setDate(date);
                                north.time.setTime(time);
                                if(north.total_flow > max)
                                {
                                    max = (ceil(north.total_flow) / 2 + 1) *2;
                                }
                                if(north.sh_flow > max)
                                {
                                    max = (ceil(north.sh_flow) / 2 + 1) *2;
                                }
                                if(north.sz_flow > max)
                                {
                                    max = (ceil(north.sz_flow) / 2 + 1) *2;
                                }

                                if(north.total_flow < min)
                                {
                                    min =  (-1) * (ceil(fabs(north.total_flow)) / 2 + 1) *2;
                                }
                                if(north.sh_flow < min)
                                {
                                    min = (-1) * (ceil(fabs(north.sh_flow)) / 2 + 1) *2;
                                }
                                if(north.sz_flow < min)
                                {
                                    min = (-1) * (ceil(fabs(north.sz_flow)) / 2 + 1) *2;
                                }

                                list.append(north);
                            }
                            emit signalUpdateNorthBoundList(list, max, min);
                        }
                    }
                }

            }
        }



        QThread::sleep(30);
    }
